    private void btn_cekActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_cekActionPerformed
        // TODO add your handling code here:
        String tgl, bul;
        int ntgl, nbul;
        
        tgl = txt_tgl.getText();
        bul = txt_bln.getText();
        
        if (checkUtil("Tanggal", tgl) && checkUtil("Bulan", bul))
        {
            ntgl = Integer.parseInt(tgl);
            nbul = Integer.parseInt(bul);
            
            if(ntgl > 31 || nbul > 12)
            {
                JOptionPane.showMessageDialog(this, "Anda salah input Tanggal dan Bulan");
                txt_out_tgl.setText("Tanggal Lahir Anda  = ");
                txt_out_zodiak.setText("Zodiak Anda Adalah = ");
            }
            else if (nbul == 2 && ntgl > 29)
            {
                JOptionPane.showMessageDialog(this, "Anda salah input Tanggal dan Bulan");
                txt_out_tgl.setText("Tanggal Lahir Anda  = ");
                txt_out_zodiak.setText("Zodiak Anda Adalah = ");
            }
            else if (nbul == 4 && ntgl > 30)
            {
                JOptionPane.showMessageDialog(this, "Anda salah input Tanggal dan Bulan");
                txt_out_tgl.setText("Tanggal Lahir Anda  = ");
                txt_out_zodiak.setText("Zodiak Anda Adalah = ");
            }
            else if (nbul == 6 && ntgl > 30)
            {
                JOptionPane.showMessageDialog(this, "Anda salah input Tanggal dan Bulan");
                txt_out_tgl.setText("Tanggal Lahir Anda  = ");
                txt_out_zodiak.setText("Zodiak Anda Adalah = ");
            }
            else if (nbul == 9 && ntgl > 30)
            {
                JOptionPane.showMessageDialog(this, "Anda salah input Tanggal dan Bulan");
                txt_out_tgl.setText("Tanggal Lahir Anda  = ");
                txt_out_zodiak.setText("Zodiak Anda Adalah = ");
            }
            else if (nbul == 11 && ntgl > 30)
            {
                JOptionPane.showMessageDialog(this, "Anda salah input Tanggal dan Bulan");
                txt_out_tgl.setText("Tanggal Lahir Anda  = ");
                txt_out_zodiak.setText("Zodiak Anda Adalah = ");
            }
            else
            {
                String strBul = CekBulan(nbul);
                String strZoo = CekZodiak(ntgl, nbul);
                txt_out_tgl.setText("Tanggal Lahir Anda  = " + tgl + " " + strBul);
                txt_out_zodiak.setText("Zodiak Anda Adalah = " + strZoo);
            }
        }
    }//GEN-LAST:event_btn_cekActionPerformed

