    private boolean isNumeric(String str)
    {
        return str != null && str.matches("[0-9.]+");
    }
    
    private boolean checkUtil(String name, String value)
    {
        boolean isOk = false;
        if (value.isEmpty())
        {
            JOptionPane.showMessageDialog(this, name + " wajib diisi");
        }
        else
        {
            if (isNumeric(value))
            {
                isOk = true;
            }
            else
            {
                JOptionPane.showMessageDialog(this, name + " Harus Diisi Angka");
            }
        }
        
        return isOk;
    }
    
    private String CekBulan(int tgl)
    {
        String bln;
        switch(tgl) {
            case 1:
                bln = "Januari";
                break;
            case 2:
                bln = "Februari";
                break;
            case 3:
                bln = "Maret";
                break;
            case 4:
                bln = "April";
                break;
            case 5:
                bln = "Mei";
                break;
            case 6:
                bln = "Juni";
                break;
            case 7:
                bln = "Juli";
                break;
            case 8:
                bln = "Agustus";
                break;
            case 9:
                bln = "September";
                break;
            case 10:
                bln = "Oktober";
                break;
            case 11:
                bln = "November";
                break;
            default: 
                bln = "Desember";
                break;
        }
        
        return bln;
    }
    
    private String CekZodiak(int tgl, int bln)
    {
        String out = null;
        if ((tgl >= 21 && bln == 3) || (tgl <= 19 && bln == 4))
        {
            out = "Aries";
        }
        else if ((tgl >= 20 && bln == 4) || (tgl <= 20 && bln == 5))
        {
            out = "Taurus";
        }
        else if ((tgl >= 21 && bln == 5) || (tgl <= 20 && bln == 6))
        {
            out = "Gemini";
        }
        else if ((tgl >= 21 && bln == 6) || (tgl <= 22 && bln == 7))
        {
            out = "Cancer";
        }
        else if ((tgl >= 23 && bln == 7) || (tgl <= 22 && bln == 8))
        {
            out = "Leo";
        }
        else if ((tgl >= 23 && bln == 8) || (tgl <= 22 && bln == 9))
        {
            out = "Virgo";
        }
        else if ((tgl >= 23 && bln == 9) || (tgl <= 22 && bln == 10))
        {
            out = "Libra";
        }
        else if ((tgl >= 23 && bln == 10) || (tgl <= 21 && bln == 11))
        {
            out = "Scorpio";
        }
        else if ((tgl >= 22 && bln == 11) || (tgl <= 21 && bln == 12))
        {
            out = "Sagitarius";
        }
        else if ((tgl >= 22 && bln == 12) || (tgl <= 19 && bln == 1))
        {
            out = "Capricorn";
        }
        else if ((tgl >= 20 && bln == 1) || (tgl <= 18 && bln == 2))
        {
            out = "Aquarius";
        }
        else if ((tgl >= 19 && bln == 2) || (tgl <= 20 && bln == 3))
        {
            out = "Pisces";
        }
        return out;
    }

